  float driftRate;    // Drift rate in ppm
  uint32_t syncCount; // Number of synchronizations
} RTC_Stats;

void updateStats(int32_t drift) {
  static RTC_Stats stats = {0};
  stats.maxDrift = max(stats.maxDrift, abs(drift));
  stats.driftRate = (float)drift / currentTime.totalSeconds * 1e6;
  stats.syncCount++;
}
